mapper.insert:

insert into ONL_CART (ID, ONLINE_SPEC_ID, ONLINE_ID, 
  USER_ID, CART_COUNT, JOIN_TIME
  )
values (#{id,jdbcType=BIGINT}, #{onlineSpecId,jdbcType=BIGINT}, #{onlineId,jdbcType=BIGINT}, 
  #{userId,jdbcType=BIGINT}, #{cartCount,jdbcType=INTEGER}, #{joinTime,jdbcType=TIMESTAMP}
  )

mapper.select:

select count(*) 
from ONL_CART
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from ONL_CART


    and ID = #{id,jdbcType=BIGINT}


    and ONLINE_SPEC_ID = #{onlineSpecId,jdbcType=BIGINT}


    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}


    and USER_ID = #{userId,jdbcType=BIGINT}


    and CART_COUNT = #{cartCount,jdbcType=INTEGER}


    and JOIN_TIME = #{joinTime,jdbcType=TIMESTAMP}


select count(1) 
from ONL_CART


    and ONLINE_SPEC_ID = #{onlineSpecId,jdbcType=BIGINT}


    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}


    and USER_ID = #{userId,jdbcType=BIGINT}


    and CART_COUNT = #{cartCount,jdbcType=INTEGER}


    and JOIN_TIME = #{joinTime,jdbcType=TIMESTAMP}


select 

from ONL_CART


    and ONLINE_SPEC_ID = #{onlineSpecId,jdbcType=BIGINT}


    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}


    and USER_ID = #{userId,jdbcType=BIGINT}

mapper.update:
  	UPDATE ONL_CART SET CART_COUNT = #{cartCount,jdbcType=INTEGER} WHERE 1=1
	AND ONLINE_SPEC_ID = #{onlineSpecId,jdbcType=BIGINT}
    AND ONLINE_ID = #{onlineId,jdbcType=BIGINT}
