mapper.select:

select count(*) 
from ONL_ONLINE
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from ONL_ONLINE


    and ID = #{id,jdbcType=BIGINT}


    and SUBJECT_TYPE = #{subjectType,jdbcType=TINYINT}


    and ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


    and ONLINE_DETAIL = #{onlineDetail,jdbcType=VARCHAR}


    and OP_ID = #{opId,jdbcType=BIGINT}


    and ONLINE_STATE = #{onlineState,jdbcType=TINYINT}


    and ONLINE_TIME = #{onlineTime,jdbcType=TIMESTAMP}


    and PRODUCT_ID = #{productId,jdbcType=BIGINT}


    and SUPPLIER_ID = #{supplierId,jdbcType=BIGINT}


    and SUPPLIER_SETTLE_TYPE = #{supplierSettleType,jdbcType=TINYINT}


select count(1) 
from ONL_ONLINE


    and SUBJECT_TYPE = #{subjectType,jdbcType=TINYINT}


    and ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


    and ONLINE_DETAIL = #{onlineDetail,jdbcType=VARCHAR}


    and OP_ID = #{opId,jdbcType=BIGINT}


    and ONLINE_STATE = #{onlineState,jdbcType=TINYINT}


    and ONLINE_TIME = #{onlineTime,jdbcType=TIMESTAMP}


    and PRODUCT_ID = #{productId,jdbcType=BIGINT}


    and SUPPLIER_ID = #{supplierId,jdbcType=BIGINT}


    and SUPPLIER_SETTLE_TYPE = #{supplierSettleType,jdbcType=TINYINT}


SELECT oo.*, oop.ONLINE_ID FROM ONL_ONLINE oo LEFT JOIN ONL_ONLINE_PROMOTION oop ON oo.ID = oop.ONLINE_ID


    and oo.ONLINE_TITLE LIKE '%${onlineTitle}%'


    and oo.ONLINE_DETAIL = #{onlineDetail,jdbcType=VARCHAR}


    and oo.ONLINE_STATE = #{onlineState,jdbcType=TINYINT}


    and oo.ONLINE_TIME = #{onlineTime,jdbcType=TIMESTAMP}
