mapper.select:

select count(*) 
from ONL_ONLINE
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from ONL_ONLINE


    and ID = #{id,jdbcType=BIGINT}


    and ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


    and ONLINE_DETAIL = #{onlineDetail,jdbcType=VARCHAR}


    and ONLINE_STATE = #{onlineState,jdbcType=TINYINT}


    and OP_ID = #{opId,jdbcType=BIGINT}


    and ONLINE_TIME = #{onlineTime,jdbcType=TIMESTAMP}


    and PRODUCT_ID = #{productId,jdbcType=BIGINT}


    and SUBJECT_TYPE = #{subjectType,jdbcType=TINYINT}


SELECT oo.*, oop.ONLINE_ID FROM ONL_ONLINE oo LEFT JOIN ONL_ONLINE_PROMOTION oop ON oo.ID = oop.ONLINE_ID


    and oo.ONLINE_TITLE LIKE '%${onlineTitle}%'


    and oo.ONLINE_DETAIL = #{onlineDetail,jdbcType=VARCHAR}


    and oo.ONLINE_STATE = #{onlineState,jdbcType=TINYINT}


    and oo.ONLINE_TIME = #{onlineTime,jdbcType=TIMESTAMP}

SELECT oo.ID, oo.ONLINE_TITLE, oopc.PIC_PATH, oos.ONLINE_SPEC, oos.SALE_PRICE, oos.CASHBACK_AMOUNT, oos.ID AS SPEC_ID, oo.ONLINE_DETAIL FROM ONL_ONLINE oo LEFT JOIN ONL_ONLINE_SPEC oos ON oos.ONLINE_ID = oo.ID LEFT JOIN ONL_ONLINE_PIC oopc ON oo.ID=oopc.ONLINE_ID WHERE 1=1 AND oos.ID IN ( SELECT ID FROM ONL_ONLINE_SPEC WHERE SALE_PRICE=(SELECT min(oos.SALE_PRICE) FROM ONL_ONLINE_SPEC oos WHERE oos.ONLINE_ID = oo.ID) AND SEQ_NO=(SELECT MIN(oos.SEQ_NO) FROM ONL_ONLINE_SPEC oos WHERE oos.ONLINE_ID = oo.ID) ) AND oo.ONLINE_STATE=1 AND oopc.PIC_TYPE=1
AND oo.ID = #{id,jdbcType=BIGINT}
AND oo.ONLINE_TITLE LIKE '%${onlineTitle}%'
AND oo.SUBJECT_TYPE = '${subjectType}'
order by ${sortname} ${sortOrder} LIMIT ${start}, ${size}
