mapper.select:

select count(*) 
from ONL_ONLINE
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from ONL_ONLINE

  
    and ID = #{id,jdbcType=BIGINT}
  
  
    and ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}
  
  
    and ONLINE_DETAIL = #{onlineDetail,jdbcType=VARCHAR}
  
  
    and ONLINE_STATE = #{onlineState,jdbcType=TINYINT}
  
  
    and OP_ID = #{opId,jdbcType=BIGINT}
  
  
    and ONLINE_TIME = #{onlineTime,jdbcType=TIMESTAMP}
  
  
    and PRODUCE_ID = #{produceId,jdbcType=BIGINT}


SELECT oo.*, oop.ONLINE_ID FROM ONL_ONLINE oo LEFT JOIN ONL_ONLINE_PROMOTION oop ON oo.ID = oop.ONLINE_ID

  
    and oo.ONLINE_TITLE LIKE '%${onlineTitle}%'
  
  
    and oo.ONLINE_DETAIL = #{onlineDetail,jdbcType=VARCHAR}
  
  
    and oo.ONLINE_STATE = #{onlineState,jdbcType=TINYINT}
  
  
    and oo.ONLINE_TIME = #{onlineTime,jdbcType=TIMESTAMP}

  	SELECT oo.ID, oo.ONLINE_TITLE, oopc.PIC_PATH, oos.ONLINE_SPEC, oos.SALE_PRICE, oos.CASHBACK_AMOUNT, oos.ID AS SPEC_ID, oo.ONLINE_DETAIL
	FROM ONL_ONLINE oo
	LEFT JOIN ONL_ONLINE_SPEC oos ON oos.ID = (
		SELECT MIN(ID) FROM ONL_ONLINE_SPEC WHERE SALE_PRICE=(SELECT min(SALE_PRICE) FROM ONL_ONLINE_SPEC WHERE ONLINE_ID=oo.ID)
	)
	LEFT JOIN ONL_ONLINE_PIC oopc ON oo.ID=oopc.ONLINE_ID
	WHERE 1=1 AND oo.ID IN (
		SELECT ONLINE_ID FROM ONL_ONLINE_SPEC WHERE SALE_PRICE=(SELECT min(SALE_PRICE) FROM ONL_ONLINE_SPEC WHERE ONLINE_ID = oo.ID)
	) AND oo.ONLINE_STATE=1 AND oopc.PIC_TYPE=1
	
		AND oo.ID = #{id,jdbcType=BIGINT}
	
	
		order by ${sortname} ${sortOrder} LIMIT ${start}, ${size}

  	SELECT oo.ID, oo.ONLINE_TITLE, oopc.PIC_PATH, oos.ONLINE_SPEC, oos.SALE_PRICE, oos.CASHBACK_AMOUNT, oos.ID AS SPEC_ID, oo.ONLINE_DETAIL
	FROM ONL_ONLINE oo
	LEFT JOIN ONL_ONLINE_SPEC oos ON oos.ID = (
		SELECT MIN(ID) FROM ONL_ONLINE_SPEC WHERE SALE_PRICE=(SELECT min(SALE_PRICE) FROM ONL_ONLINE_SPEC WHERE ONLINE_ID=oo.ID)
	)
	LEFT JOIN ONL_ONLINE_PIC oopc ON oo.ID=oopc.ONLINE_ID
	WHERE 1=1 AND oo.ID IN (
		SELECT ONLINE_ID FROM ONL_ONLINE_SPEC WHERE SALE_PRICE=(SELECT min(SALE_PRICE) FROM ONL_ONLINE_SPEC WHERE ONLINE_ID = oo.ID)
	) AND oo.ONLINE_STATE=1 AND oopc.PIC_TYPE=1 AND oo.ID=#{id,jdbcType=BIGINT}
