mapper.insert:

insert into ONL_ONLINE_PROMOTION (ID, ONLINE_ID, PROMOTION_TYPE
  )
values (#{id,jdbcType=BIGINT}, #{onlineId,jdbcType=BIGINT}, #{promotionType,jdbcType=TINYINT}
  )

mapper.update:

update ONL_ONLINE_PROMOTION
set ONLINE_ID = #{onlineId,jdbcType=BIGINT},
  PROMOTION_TYPE = #{promotionType,jdbcType=TINYINT}
where ID = #{id,jdbcType=BIGINT}

mapper.select:

select 

from ONL_ONLINE_PROMOTION


    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}


    and PROMOTION_TYPE = #{promotionType,jdbcType=TINYINT}


select count(*) 
from ONL_ONLINE_PROMOTION
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from ONL_ONLINE_PROMOTION


    and ID = #{id,jdbcType=BIGINT}


    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}


    and PROMOTION_TYPE = #{promotionType,jdbcType=TINYINT}


select count(1) 
from ONL_ONLINE_PROMOTION


    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}


    and PROMOTION_TYPE = #{promotionType,jdbcType=TINYINT}
