mapper.select:

select 

from ONL_ONLINE_SPEC

  
    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}
  
  
    and PRODUCT_SPEC_ID = #{productSpecId,jdbcType=BIGINT}
  
  
    and ONLINE_SPEC = #{onlineSpec,jdbcType=VARCHAR}
  
  
    and SALE_PRICE = #{salePrice,jdbcType=DECIMAL}
  
  
    and COST_PRICE = #{costPrice,jdbcType=DECIMAL}
  
  
    and CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}
  
  
    and COMMISSION_AMOUNT = #{commissionAmount,jdbcType=DECIMAL}
  
  
    and BUY_POINT = #{buyPoint,jdbcType=DECIMAL}
  
  
    and SALE_UNIT = #{saleUnit,jdbcType=VARCHAR}
  
  
    and CURRENT_ONLINE_COUNT = #{currentOnlineCount,jdbcType=INTEGER}
  
  
    and LIMIT_COUNT = #{limitCount,jdbcType=INTEGER}
  
  
    and SALE_COUNT = #{saleCount,jdbcType=INTEGER}
  
  
    and SEQ_NO = #{seqNo,jdbcType=INTEGER}


select count(*) 
from ONL_ONLINE_SPEC
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from ONL_ONLINE_SPEC

  
    and ID = #{id,jdbcType=BIGINT}
  
  
    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}
  
  
    and PRODUCT_SPEC_ID = #{productSpecId,jdbcType=BIGINT}
  
  
    and ONLINE_SPEC = #{onlineSpec,jdbcType=VARCHAR}
  
  
    and SALE_PRICE = #{salePrice,jdbcType=DECIMAL}
  
  
    and COST_PRICE = #{costPrice,jdbcType=DECIMAL}
  
  
    and CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}
  
  
    and COMMISSION_AMOUNT = #{commissionAmount,jdbcType=DECIMAL}
  
  
    and BUY_POINT = #{buyPoint,jdbcType=DECIMAL}
  
  
    and SALE_UNIT = #{saleUnit,jdbcType=VARCHAR}
  
  
    and CURRENT_ONLINE_COUNT = #{currentOnlineCount,jdbcType=INTEGER}
  
  
    and LIMIT_COUNT = #{limitCount,jdbcType=INTEGER}
  
  
    and SALE_COUNT = #{saleCount,jdbcType=INTEGER}
  
  
    and SEQ_NO = #{seqNo,jdbcType=INTEGER}


select count(1) 
from ONL_ONLINE_SPEC

  
    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}
  
  
    and PRODUCT_SPEC_ID = #{productSpecId,jdbcType=BIGINT}
  
  
    and ONLINE_SPEC = #{onlineSpec,jdbcType=VARCHAR}
  
  
    and SALE_PRICE = #{salePrice,jdbcType=DECIMAL}
  
  
    and COST_PRICE = #{costPrice,jdbcType=DECIMAL}
  
  
    and CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}
  
  
    and COMMISSION_AMOUNT = #{commissionAmount,jdbcType=DECIMAL}
  
  
    and BUY_POINT = #{buyPoint,jdbcType=DECIMAL}
  
  
    and SALE_UNIT = #{saleUnit,jdbcType=VARCHAR}
  
  
    and CURRENT_ONLINE_COUNT = #{currentOnlineCount,jdbcType=INTEGER}
  
  
    and LIMIT_COUNT = #{limitCount,jdbcType=INTEGER}
  
  
    and SALE_COUNT = #{saleCount,jdbcType=INTEGER}
  
  
    and SEQ_NO = #{seqNo,jdbcType=INTEGER}

SELECT oos.ID, oos.ONLINE_SPEC, oos.SALE_PRICE, oos.CASHBACK_AMOUNT, oos.CURRENT_ONLINE_COUNT, oos.SALE_COUNT, oop.PIC_PATH
FROM ONL_ONLINE_SPEC oos
LEFT JOIN ONL_ONLINE_PIC oop ON
oos.ONLINE_ID=oop.ONLINE_ID AND oop.PIC_TYPE=1
WHERE

	oos.ONLINE_ID=#{onlineId,jdbcType=INTEGER}


	AND oos.ID=#{id,jdbcType=BIGINT}


	AND oos.ONLINE_SPEC=#{onlineSpec,jdbcType=VARCHAR}

ORDER BY oos.SEQ_NO ASC
